                        var pitch_start = parseFloat(measure.pitch_start);
                        var yaw_end = parseFloat(measure.yaw_end);
                        var pitch_end = parseFloat(measure.pitch_end);
                        var tmp_coords = mid_point([yaw_start,pitch_start],[yaw_end,pitch_end]);
